INSERT INTO `creature_ai_texts` VALUES (-1060, 'I\'ve stood with some of you draenei before at Skettis, and others in Shadowmoon Valley. But now we\'re on Azeroth, and the situation looks grim.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25162');
INSERT INTO `creature_ai_texts` VALUES (-1061, 'First though, you sin\'dorei listen up. I run a tight ship... this isn\'t going to be like what you\'re used to with the Scryers. Pull your weight, or get back on the boat!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25162');
INSERT INTO `creature_ai_texts` VALUES (-1062, 'Now that that\'s out of the way, welcome to the Isle of Quel\'Danas. The Sunwell\'s on the plateau to the south, and Prince Kael\'thas is holed up in the Magisters\' Terrace to the east.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25162');
INSERT INTO `creature_ai_texts` VALUES (-1063, 'We\'re here as support for the so-called \'adventurers\' that the brass called in. We\'ll help them hold the key points on the island, and keep the heat off so that they can do their jobs.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25162');
INSERT INTO `creature_ai_texts` VALUES (-1064, 'If none of you ladies have any questions, you can get further instruction and assignments from your squad leaders. That\'s it. ', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25162');
INSERT INTO `creature_ai_texts` VALUES (-1065, 'Good luck!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25162');
INSERT INTO `creature_ai_texts` VALUES (-1066, 'Welcome to the Sunwell. Despite the mild temperatures and scenic vistas, you\'re not on vacation, ladies and gentlemen!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25162');
INSERT INTO `creature_ai_texts` VALUES (-1067, 'The current situation looks like this: we\'re holding what we can while those with more experience and better equipment are moving against Prince Kael\'thas.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25162');
INSERT INTO `creature_ai_texts` VALUES (-1068, 'I know, we thought he was dead too, but that\'s not the case. He\'s trying to use the Sunwell to bring the master of the Burning Legion, Kil\'jaeden, into this world.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25162');
INSERT INTO `creature_ai_texts` VALUES (-1069, 'Let me assure you people that if he\'s successful, this world will suffer far worse than Outland ever did! So let\'s make sure that doesn\'t come to pass.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25162');
INSERT INTO `creature_ai_texts` VALUES (-1070, 'Warriors will be deployed to hold key positions, while you marksmen will be assigned to keep the enemy fliers at bay.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25162');
INSERT INTO `creature_ai_texts` VALUES (-1071, 'I\'m awaiting orders on your specific assignments. In the meantime, stay here and be ready to move at a moment\'s notice!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25162');
INSERT INTO `creature_ai_texts` VALUES (-1072, 'Hello to you new recruits. I\'m Drill Sergeant Bahduum. For the next few days, you\'re going to receive orientation.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25162');
INSERT INTO `creature_ai_texts` VALUES (-1073, 'The Isle of Quel\'Danas has been overrun by the forces of Prince Kael\'thas Sunstrider. Intel had him pegged as deceased. Intel was wrong.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25162');
INSERT INTO `creature_ai_texts` VALUES (-1074, 'Up on the plateau, he and his demon friends are trying to reignite the Sunwell and open a rift to bring their head-honcho, Kil\'Jaeden back into the world.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25162');
INSERT INTO `creature_ai_texts` VALUES (-1075, 'Our job is to clear out as much of the fodder as we can, while supporting the specialists that the powers-that-be are having brought in.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25162');
INSERT INTO `creature_ai_texts` VALUES (-1076, 'Needless to say, a lot of us won\'t be going home. But if you remember your training and keep your wits about you, you\'ll greatly increase your chances.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25162');
