INSERT INTO `creature_ai_texts` VALUES (-1262, 'The shadow... will engulf you', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11253, 1, 0, 0, '20870');
INSERT INTO `creature_ai_texts` VALUES (-1263, 'On guard!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11241, 1, 0, 0, '20886');
INSERT INTO `creature_ai_texts` VALUES (-1264, 'Defend yourself, for all the good it will do...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11242, 1, 0, 0, '20886');
INSERT INTO `creature_ai_texts` VALUES (-1265, 'Knew this was... the only way out', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11243, 1, 0, 0, '20886');
INSERT INTO `creature_ai_texts` VALUES (-1266, 'Yes, that was quite satisfying', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11239, 1, 0, 0, '20886');
INSERT INTO `creature_ai_texts` VALUES (-1267, 'At last, a target for my frustrations!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11238, 1, 0, 0, '20886');
INSERT INTO `creature_ai_texts` VALUES (-1268, 'Have you come to kill Dalliah? Can I watch?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11237, 1, 0, 0, '20886');
INSERT INTO `creature_ai_texts` VALUES (-1269, 'It is unwise to anger me.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, '20885');
INSERT INTO `creature_ai_texts` VALUES (-1270, 'You chose the wrong opponent.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11088, 1, 0, 0, '20885');
INSERT INTO `creature_ai_texts` VALUES (-1271, 'Completely ineffective. Just like someone else I know.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11087, 1, 0, 0, '20885');
INSERT INTO `creature_ai_texts` VALUES (-1272, 'Now I\'m really... angry...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11093, 1, 0, 0, '20885');
INSERT INTO `creature_ai_texts` VALUES (-1273, 'Reap the Whirlwind!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, '20885');
INSERT INTO `creature_ai_texts` VALUES (-1274, 'I\'ll cut you to pieces!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '20885');
