INSERT INTO `creature_ai_texts` VALUES (-1276, 'Ahh... Just what I needed.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '20885');
INSERT INTO `creature_ai_texts` VALUES (-1277, 'Step forward! I will see that you are properly welcomed', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11071, 1, 0, 0, '17976');
INSERT INTO `creature_ai_texts` VALUES (-1278, 'Oh stop your whimpering', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11072, 1, 0, 0, '17976');
INSERT INTO `creature_ai_texts` VALUES (-1279, 'I\'ve not yet.. begun to..', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11079, 1, 0, 0, '17976');
INSERT INTO `creature_ai_texts` VALUES (-1280, 'You are no longer dealing with some underling.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11076, 1, 0, 0, '17976');
INSERT INTO `creature_ai_texts` VALUES (-1281, 'Band\'or shorel\'aran!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11077, 1, 0, 0, '17976');
INSERT INTO `creature_ai_texts` VALUES (-1282, 'Guards! Come and kill these intruders!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11078, 1, 0, 0, '17976');
INSERT INTO `creature_ai_texts` VALUES (-1283, 'What aggravation is this? You will die!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11205, 1, 0, 0, '17978');
INSERT INTO `creature_ai_texts` VALUES (-1284, 'I hate to say I told you so...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11207, 1, 0, 0, '17978');
INSERT INTO `creature_ai_texts` VALUES (-1285, 'You won\'t... get far', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11212, 1, 0, 0, '17978');
INSERT INTO `creature_ai_texts` VALUES (-1286, 'I revel in your pain!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11209, 1, 0, 0, '17978');
