INSERT INTO `creature_ai_texts` VALUES (-1312, 'The Legion\'s final conquest has begun! Once again the subjugation of this world is within our grasp. Let none survive!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11022, 1, 0, 0, '17767');
