INSERT INTO `creature_ai_texts` VALUES (-1359, 'He asked if the imp could join in... can you believe it? Actually, it wasn\'t half bad...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '16460');
