INSERT INTO `creature_ai_texts` VALUES (-800, 'The numbers of my companions dwindles, goddess, and my own power shall soon be insufficient to hold back the demons of Felwood.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Velinde Starsong for Quest 1022');
INSERT INTO `creature_ai_texts` VALUES (-801, 'Goddess, grant me the power to overcome my enemies! Hear me, please, my need is desperate and my cause is just!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Velinde Starsong for Quest 1022');
INSERT INTO `creature_ai_texts` VALUES (-802, 'What... what is this? Could this be the answer to my prayers? Elune has granted me a weapon--this scythe--to defeat the demons.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Velinde Starsong for Quest 1022');
INSERT INTO `creature_ai_texts` VALUES (-803, 'Anaya...? Do my eyes deceive me? Is it really you?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '3644 for Quest 963');
INSERT INTO `creature_ai_texts` VALUES (-804, 'That fates should be so cruel as to permit us only this after a thousand years apart...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '3644 for Quest 963');
INSERT INTO `creature_ai_texts` VALUES (-805, 'Do you hate me, my love? That I was forced to destroy your living form, that your spirit be released from unhappy bondage.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '3644 for Quest 963');
INSERT INTO `creature_ai_texts` VALUES (-806, 'No! Anaya... Anaya! Don\'t leave me! Please...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '3644 for Quest 963');
INSERT INTO `creature_ai_texts` VALUES (-807, 'How, my love? How will I find the strength to face the ages of the world without you by my side...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '3644 for Quest 963');
INSERT INTO `creature_ai_texts` VALUES (-808, 'The ages have been cruel to you and I, my love, but be assured, it is, and at long last we are reunited.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '3843 for Quest 963');
INSERT INTO `creature_ai_texts` VALUES (-809, 'Let it not trouble your heart, beloved. You have freed me from slavery, and for that I love you all the more.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '3843 for Quest 963');
INSERT INTO `creature_ai_texts` VALUES (-810, 'Sadly, even this must be cut short... The ties that bind me to this world weaken, and pull me away...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '3843 for Quest 963');
INSERT INTO `creature_ai_texts` VALUES (-811, 'Farewell, Cerellean, until we are joined once again...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '3843 for Quest 963');
INSERT INTO `creature_ai_texts` VALUES (-812, 'Anaya\'s soft voice trails away into the mists, \"Know that I love you always...\"', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, '3843 for Quest 963');
INSERT INTO `creature_ai_texts` VALUES (-650002, 'It\'s NOW!!!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Unworthy Initiate Say on Aggro');
