INSERT INTO `creature_ai_texts` VALUES (-650006, 'And now you die!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Unworthy Initiate Say at 50-30%');
INSERT INTO `creature_ai_texts` VALUES (-650007, 'I was a soldier of the Light once... Look at what I have become...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Unworthy Initiate Say at 20-15%');
