INSERT INTO `creature_ai_texts` VALUES (-50000, 'Hey there, don\'t be alarmed. It\'s me... you... from the future. I\'m here to help.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '27899 (Quest 12470)');
INSERT INTO `creature_ai_texts` VALUES (-50001, 'Heads up... here they come. I\'ll help as much as I can. Let\'s just keep them off the hourglass!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '27899 (Quest 12470)');
INSERT INTO `creature_ai_texts` VALUES (-50002, 'No matter what, you can\'t die, because would mean that I would cease to exist, right? But, I was here before when I was you. I\'m so confused!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '27899 (Quest 12470)');
INSERT INTO `creature_ai_texts` VALUES (-50003, 'Look at you fight; no wonder I turned to drinking.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '27899 (Quest 12470)');
INSERT INTO `creature_ai_texts` VALUES (-50004, 'Wow, I\'d forgotten how inexperienced I used to be.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '27899 (Quest 12470)');
INSERT INTO `creature_ai_texts` VALUES (-50005, 'I can\'t believe that I used to wear that.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '27899 (Quest 12470)');
INSERT INTO `creature_ai_texts` VALUES (-50006, 'Listen. I\'m not supposed to tell you this, but there\'s going to be this party that you\'re invited to. Whatever you do, DO NOT DRINK THE PUNCH!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '27899 (Quest 12470)');
INSERT INTO `creature_ai_texts` VALUES (-50007, 'Wish I could remember how many of the Infinite Dragonflight were going to try to stop you. This fight was so long ago.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '27899 (Quest 12470)');
INSERT INTO `creature_ai_texts` VALUES (-50008, 'Look, the hourglass has revealed Nozdormu!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '27899 (Quest 12470)');
INSERT INTO `creature_ai_texts` VALUES (-50009, 'Farewell. Keep us alive and get some better equipment!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '27899 (Quest 12470)');
INSERT INTO `creature_ai_texts` VALUES (-50010, 'Whoa! You\'re me, but from the future! Hey, my equipment got an upgrade! Cool!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '32331 (Quest 13343)');
INSERT INTO `creature_ai_texts` VALUES (-50011, 'Here come the Infinites! I\'ve got to keep the hourglass safe. Can you help?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '32331 (Quest 13343)');
INSERT INTO `creature_ai_texts` VALUES (-50012, 'This equipment looks cool and all, but couldn\'t we have done a little better? Are you even raiding?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '32331 (Quest 13343)');
INSERT INTO `creature_ai_texts` VALUES (-50013, 'Chromie said that if I don\'t do this just right, I might wink out of existence. If I go, then you go!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '32331 (Quest 13343)');
INSERT INTO `creature_ai_texts` VALUES (-50014, 'I just want you to know that if we get through this alive, I\'m making sure that we turn out better than you. No offense.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '32331 (Quest 13343)');
INSERT INTO `creature_ai_texts` VALUES (-50015, 'I can\'t believe that I used to wear that.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '32331 (Quest 13343)');
INSERT INTO `creature_ai_texts` VALUES (-50016, 'Looks like I\'m an underachiever.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '32331 (Quest 13343)');
INSERT INTO `creature_ai_texts` VALUES (-50017, 'I think I\'m going to turn to drinking after this.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '32331 (Quest 13343)');
INSERT INTO `creature_ai_texts` VALUES (-50018, 'What the heck? Nozdormu is up there!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '32331 (Quest 13343)');
INSERT INTO `creature_ai_texts` VALUES (-50019, 'I feel like I\'m being pulled away through time. Thanks for the help...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '32331 (Quest 13343)');
