INSERT INTO `creature_ai_texts` VALUES (-7012, 'It\'s been so long since a traveler has come here bearing flowers.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'c33273 Maiden of Drak\'Mar - all quest named \"A Worthy Weapon\"');
INSERT INTO `creature_ai_texts` VALUES (-7011, 'The lake has been too lonely these past years. The travelers stopped coming and evil came to these waters.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'c33273 Maiden of Drak\'Mar - all quest named \"A Worthy Weapon\"');
INSERT INTO `creature_ai_texts` VALUES (-7013, 'My name is Claximus Decimus Meridius. Commander of the Armies of the North. General of the Felix Legions. Loyal servant to the true emperor, Marcus Aurelius. Father to a murloc son, husband to a murloc wife and I will have my vengeance, in this life or the next.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, NULL);
INSERT INTO `creature_ai_texts` VALUES (-7014, 'You may have killed Valduran, but that will not stop me from completing the colossus.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'c30299 Overseer Narvir - q12998');
INSERT INTO `creature_ai_texts` VALUES (-7015, 'A pause...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'c30299 Overseer Narvir - q12998');
INSERT INTO `creature_ai_texts` VALUES (-7016, 'I will lay a mild compulsion upon you. Jump into the depths before me so that you put yourself into my element and thereby display your submission.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'Oacha\'noa - q12032');
INSERT INTO `creature_ai_texts` VALUES (-7018, 'Pathetic fool! A servant of malygos would sooner die than aid an emeny...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 1260, 2, 0, 0, NULL);
INSERT INTO `creature_ai_texts` VALUES (-7019, 'Aargh! Do your worst, $c! I\'ll tell you NOTHING!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 6921, 2, 0, 0, NULL);
INSERT INTO `creature_ai_texts` VALUES (-7020, 'Aahhhh! Release me! I am of no use to you. I swear it!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 1260, 2, 0, 0, NULL);
INSERT INTO `creature_ai_texts` VALUES (-7021, 'Stop! I bed you, please stop. Please....', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 6921, 2, 0, 0, NULL);
INSERT INTO `creature_ai_texts` VALUES (-7022, 'Alright! I am beaten. Your previous archmage is held in a prison, elevated and sealed. Even if you manage to reach her, Salrand herself holds the key. Your mission is folly!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 1260, 2, 0, 0, NULL);
INSERT INTO `creature_ai_texts` VALUES (-7023, 'I\'ve given you the information, $r ! You\'re wasting your time....', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 6921, 2, 0, 0, NULL);
INSERT INTO `creature_ai_texts` VALUES (-7024, 'Noooo! This tortue is inhumane! You have what you want... why don\'t you just kill me?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 1260, 2, 0, 0, NULL);
INSERT INTO `creature_ai_texts` VALUES (-7025, 'Little $N , why do you call me forth? Are you working with the trolls of this land? Have you come to kill me and take my power as your own?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'Oacha\'noa - q12032');
INSERT INTO `creature_ai_texts` VALUES (-7026, 'I sense uncertainty in you, and I do not trust it whether you are with them, or not. If you wish my augury for the Kalu\'ak, you will have to prove yourself first.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'Oacha\'noa - q12032');
INSERT INTO `creature_ai_texts` VALUES (-7027, 'Well done, $N . Your display of respect is duly noted. Now, I have information for you that you must convey to the Kalu\'ak.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'Oacha\'noa - q12032');
INSERT INTO `creature_ai_texts` VALUES (-7028, 'Simply put, you must tell the tuskarr that they cannot run. If they do so, their spirits will be destroyed by the evil rising within Northrend.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'Oacha\'noa - q12032');
INSERT INTO `creature_ai_texts` VALUES (-7029, 'Tell the mystic that his people are to stand and fight alongside the Horde and Alliance against the forces of Malygos and the Lich King.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'Oacha\'noa - q12032');
INSERT INTO `creature_ai_texts` VALUES (-7030, 'Now swim back with the knowledge I have granted you. Do what you can for them $R .', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'Oacha\'noa - q12032');
INSERT INTO `creature_ai_texts` VALUES (-7031, 'Hah ! So be it. Blow the horn of a magnataur leader at the ring of torches south of the Blue Dragonshrine. Make peace with your gods... Grom\'thar will come.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'Dregmar Runebrand - q12150');
INSERT INTO `creature_ai_texts` VALUES (-7032, 'Why do you do this? Did I somehow wrong you in life? \r\nAh, I see it now in your mind. This is the work of one of my former students... Mehlar Dawnblade. It is sad to know that his heart has turned so dark.\r\nReturn to him. Return to Mehlor and tell him that I forgive him and that I understand why he believes what he does.\r\nI can only hope that he will see the Light and instead turn his energies to restoring once-beautiful Quel\'Thalas.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Ghost of Uther Lightbringer - q9444 - c95169');
INSERT INTO `creature_ai_texts` VALUES (-7033, 'You no take... me!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 5, 'c29618 Snowblind Follower - q14141 q14090');
INSERT INTO `creature_ai_texts` VALUES (-7034, 'No! Me not afraid! Grrrrr! No kill me!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 5, 'c29618 Snowblind Follower - q14141 q14090');
INSERT INTO `creature_ai_texts` VALUES (-7035, 'Net not stop me! No... net stop me', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 5, 'c29618 Snowblind Follower - q14141 q14090');
INSERT INTO `creature_ai_texts` VALUES (-7049, 'Mayor Godfrey hisses!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'c27577 - q12321');
INSERT INTO `creature_ai_texts` VALUES (-7050, 'Tell you nothing, preacher.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'c27577 - q12321');
INSERT INTO `creature_ai_texts` VALUES (-7051, 'What can you do to me that Kel\'Thuzad has not? That the Lich King will not?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'c27577 - q12321');
INSERT INTO `creature_ai_texts` VALUES (-7052, 'The book is your salvation, yes... but nothing will you know! NOTHING I SAY! NOTHING!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'c27577 - q12321');
INSERT INTO `creature_ai_texts` VALUES (-7053, 'IT BURNSSSSS!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'c27577 - q12321');
INSERT INTO `creature_ai_texts` VALUES (-7054, 'The pain is unbearable!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'c27577 - q12321');
INSERT INTO `creature_ai_texts` VALUES (-7055, 'IT BURNSSSSS!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'c27577 - q12321');
INSERT INTO `creature_ai_texts` VALUES (-7056, 'It tells of the coming apocalypse. How this world will burn and be reborn from death.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'c27577 - q12321');
INSERT INTO `creature_ai_texts` VALUES (-7057, 'NOOOO! I tell you! I tell you everything!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'c27577 - q12321');
INSERT INTO `creature_ai_texts` VALUES (-7058, 'Humans... Beneath the earth of Wintergarde Village you built a mausoleum! Why do you think Naxxramas attacked that spot? Thel\'zan hides deep in your own crypt and sends a thousand-thousand corpses at you! Perish you will...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'c27577 - q12321');
INSERT INTO `creature_ai_texts` VALUES (-7059, 'How? Humans truly are stupid, yes? Thel\'zan the Duskbringer! Thel\'zan the Lich! He who is born of human flesh and bone, sacrificed all for power, protected by the Lich King!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'c27577 - q12321');
INSERT INTO `creature_ai_texts` VALUES (-7060, 'You cannot stop Thel\'zan! He bears the dark gift, bestowed upon him by the Lich King himself!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'c27577 - q12321');
INSERT INTO `creature_ai_texts` VALUES (-7061, 'You\'ve got my attention, dragon. You\'ll find I\'m not as easily scared as the villagers below', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 25, 'c17651 Image of Medivh - q9645');
INSERT INTO `creature_ai_texts` VALUES (-7062, 'Your dabbling in the arcane has gone too far, Medivh. You\'ve attracted the attention of powers beyond your understanding. You must leave Karazhan at once!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'c17652 Image of Arcanagos - q9645');
INSERT INTO `creature_ai_texts` VALUES (-7063, 'You dare challenge me at my own dwelling? Your arrogance is astounding, even for a dragon!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 25, 'c17651 Image of Medivh - q9645');
INSERT INTO `creature_ai_texts` VALUES (-7064, 'A dark power seeks to use you, Medivh! If you stay, dire days will follow. You must hurry, we don\'t have much time!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'c17652 Image of Arcanagos - q9645');
