INSERT INTO `creature_ai_texts` VALUES (-7067, 'Image of Medivh begins to cast a spell of great power, weaving his own essence into the magic.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'c17651 Image of Medivh - q9645');
INSERT INTO `creature_ai_texts` VALUES (-7068, 'What have you done, wizard? This cannot be! I\'m burning from... within!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'c17652 Image of Arcanagos - q9645');
