INSERT INTO `creature_ai_texts` VALUES (-7072, 'Your companion seeks the shardhorn patriarch Farunn, who holds himself aloof from his brothers.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
