INSERT INTO `creature_ai_texts` VALUES (-7074, 'Return to your companion. Tell him to go forth and claim his prize in Farunn.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
