INSERT INTO `creature_ai_texts` VALUES (-7080, 'There is word from Jotunheim. The sleep-watchers there believe that they have found someone of significance.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `creature_ai_texts` VALUES (-7081, 'Describe this vrykul. What does he look like?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
