INSERT INTO `creature_ai_texts` VALUES (-7083, 'I am not interested in excuses. Perhaps they gave you a name?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
