INSERT INTO `creature_ai_texts` VALUES (-63, 'If this soldier does not receive taken care soon will die, there will be any brave that want to help me?', NULL, NULL, NULL, NULL, NULL, 'Si este soldado no recibe cuidados pronto morirá, no habrá ningún valiente que quiera ayudarme? ', NULL, '', 0, 0, 0, 0, 'Fortaleza de Denuedo - Medico Espaina');
