INSERT INTO `creature_ai_texts` VALUES (-65, 'The fear is not an option! We have to struggle until the last drop of our blood!', NULL, NULL, NULL, NULL, NULL, 'El miedo no es una opción! debemos luchar hasta la última gota de nuestra sangre!', NULL, '', 0, 0, 0, 0, 'Fortaleza de Denuedo - Soldados');
INSERT INTO `creature_ai_texts` VALUES (-66, 'By the Alliance! By the honour! By the death! Resist to the fear do not owe him the pleasure that they snatch you the life with fear in your eyes!', NULL, NULL, NULL, NULL, NULL, 'Por la Alianza! Por el honor! por la muerte! Resistir al miedo no le debéis el placer de que os arrebaten la vida con miedo en vuestros ojos!', NULL, '', 0, 0, 0, 0, 'Fortaleza de Denuedo - Soldados');
INSERT INTO `creature_ai_texts` VALUES (-67, 'We must prepare the steel of our brave ones! You must not weaken before the effort! That the pace of your hammer resounds to the sound of the drums of war!', NULL, NULL, NULL, NULL, NULL, 'Debemos preparar el acero de nuestros valientes! no debes flaquear ante el esfuerzo! Que el ritmo de vuestro martillo retumbe al son de los tambores de guerra!', NULL, '', 0, 0, 0, 0, 'Fortaleza de Denuedo - Soldados');
INSERT INTO `creature_ai_texts` VALUES (-69, 'Do not forget to visit the neighborhood of the craftsmen! The best recipes and components for all the crafts!', NULL, NULL, NULL, NULL, NULL, 'No te olvides de visitar el barrio de los artesanos! Las mejores recetas y componentes para todas las artesanías!', NULL, '', 0, 1, 0, 0, 'Fortaleza de Denuedo - Rolling Mckrill');
INSERT INTO `creature_ai_texts` VALUES (-70, 'Filthy beasts to approach! And you will take to yourselves such a blow that you will not know where you have the head and where the feet!!', NULL, NULL, NULL, NULL, NULL, 'Acercaros bestias inmundas! y os llevareis tal escobazo que no sabréis donde tenéis la cabeza y donde los pies!!', NULL, '', 0, 1, 0, 0, 'Fortaleza de Denuedo - Wendy Darren');
INSERT INTO `creature_ai_texts` VALUES (-71, 'Since we come to these lands only we have had problems and more problems, this where the army when you need them? The one who is going to defend us from these beasts?', NULL, NULL, NULL, NULL, NULL, 'Desde que llegamos a estas tierras solo hemos tenido problemas y más problemas, donde esta el ejercito cuando los necesitas? quien va a defendernos de esas bestias?', NULL, '', 0, 0, 0, 0, 'Fortaleza de Denuedo - Geral Verde');
INSERT INTO `creature_ai_texts` VALUES (-73, 'Go, go, do not doubt it any more, our routes of flight are totally sure!', NULL, NULL, NULL, NULL, NULL, 'Vamos, vamos, no lo dudes mas, nuestras rutas de vuelo son totalmente seguras!', NULL, '', 0, 0, 0, 0, 'Bastion grito de guerra (Tundra) - Ruta de vuelo');
INSERT INTO `creature_ai_texts` VALUES (-74, 'Prepare you riders! The day of our glory approximates ! The wings of our mounts will be the most feared shadow of the skies!', NULL, NULL, NULL, NULL, NULL, 'Preparaos jinetes! El día de nuestra gloria se aproxima! Las alas de nuestras monturas serán la sombra más temida de los cielos!', NULL, '', 0, 0, 0, 0, 'Bastion grito de guerra (Tundra) - Maestro del viento TorBo');
